when.set-body:
@{
                    JArray response = context.Response.Body.As();
                    foreach(JObject item in response.Children()) {
                    foreach (var key in new [] {"members", "createdAt", "createdBy", "modifiedAt", "modifiedBy"}) {
                    try {
                    item.Property(key).Remove();
                    } catch (Exception ex) {
                    // do nothing
                    }
                    }
                    }
                    return response.ToString();
                    }
